UPDATE ai_training_exercises SET description = 'Introduction to simple food puzzles to stimulate problem-solving and maintain prolonged interest.' WHERE description = 'Introduzione a puzzle alimentari semplici per stimolare problem-solving e mantenere l''interesse prolungato.';
UPDATE ai_training_exercises SET description = 'Tactile stimulation through different surfaces to awaken curiosity and pleasure of contact.' WHERE description = 'Stimolazione tattile attraverso diverse superfici per risvegliare la curiosità e il piacere del contatto.';
